import { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import Router from 'next/router';
import axios from 'axios';
import { showSuccessMessage, showErrorMessage } from '../helpers/alerts';
import { API } from '../config';
import { isAuth } from '../helpers/auth';

const Register = () => {
    const [state, setState] = useState({
        name: 'Christopher',
        email: 'adekschris@gmail.com',
        password: 'jehoVAHjireh1',
        error: '',
        success: '',
        buttonText: 'Register',
        loadedCategories: [],
        categories: []
    });

    const { name, email, password, error, success, buttonText, loadedCategories, categories } = state;

    useEffect(() => {
        isAuth() && Router.push('/');
    }, []);

    // load categories when component mounts using useEffect
    useEffect(() => {
        loadCategories();
    }, []);

    const loadCategories = async () => {
        const response = await axios.get(`${API}/categories`);
        setState({ ...state, loadedCategories: response.data });
    };

    const handleToggle = c => () => {
        // return the first index or -1
        const clickedCategory = categories.indexOf(c);
        const all = [...categories];

        if (clickedCategory === -1) {
            all.push(c);
        } else {
            all.splice(clickedCategory, 1);
        }
        console.log('all >> categories', all);
        setState({ ...state, categories: all, success: '', error: '' });
    };

    // show categories > checkbox
    const showCategories = () => {
        return (
            loadedCategories &&
            loadedCategories.map((c, i) => (
                <li className="list-unstyled" key={c._id}>
                    <input type="checkbox" onChange={handleToggle(c._id)} className="mr-2" />
                    <label className="form-check-label">{c.name}</label>
                </li>
            ))
        );
    };

    const handleChange = name => e => {
        setState({ ...state, [name]: e.target.value, error: '', success: '', buttonText: 'Register' });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        console.table({
            name,
            email,
            password,
            categories
        });
        setState({ ...state, buttonText: 'Registering...' });
        try {
            const response = await axios.post(`${API}/register`, {
                name,
                email,
                password,
                categories
            });
            console.log(response);
            setState({
                ...state,
                name: '',
                email: '',
                password: '',
                buttonText: 'Submitted',
                success: response.data.message
            });
        } catch (error) {
            console.log(error);
            setState({ ...state, buttonText: 'Register', error: error.response.data.error });
        }
    };



    const registerForm = () => (
        <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
                <input
                    value={name}
                    onChange={handleChange('name')}
                    type="text"
                    className="form-control p-3"
                    placeholder="Type your name"
                    required
                />
            </div>
            <div className="form-group mb-3">
                <input
                    value={email}
                    onChange={handleChange('email')}
                    type="email"
                    className="form-control p-3"
                    placeholder="Type your email"
                    required
                />
            </div>
            <div className="form-group ">
                <input
                    value={password}
                    onChange={handleChange('password')}
                    type="password"
                    className="form-control p-3"
                    placeholder="Type your password"
                    required
                />
            </div>

            <div className="form-group mt-4">
                <label className="text-muted ml-4">Category</label>
                <ul style={{ maxHeight: '100px', overflowY: 'scroll' }}>{showCategories()}</ul>
            </div>

            <div className="form-group">
                <button className="btn btn-primary px-4">{buttonText}</button>
            </div>
        </form>
    );

    return (
        <Layout>
            <div className='row'>


                <div className="col-md-6 p-5">
                    <h1>Register</h1>
                    <br />
                    {success && showSuccessMessage(success)}
                    {error && showErrorMessage(error)}
                    {registerForm()}
                </div>
            </div>
        </Layout>
    );
};

export default Register;
