// imports
import { useState, useEffect } from 'react';
import Layout from '../../../components/Layout';
import axios from 'axios';
import withUser from '../../withUser';
import { getCookie, isAuth } from '../../../helpers/auth';
import { API } from '../../../config';
import { showSuccessMessage, showErrorMessage } from '../../../helpers/alerts';

const Update = ({ oldLink, token }) => {
    // state
    const [state, setState] = useState({
        title: oldLink.title,
        url: oldLink.url,
        categories: oldLink.categories,
        loadedCategories: [],
        success: '',
        error: '',
        type: oldLink.type,
        medium: oldLink.medium
    });

    const { title, url, categories, loadedCategories, success, error, type, medium } = state;

    // load categories when component mounts using useEffect
    useEffect(() => {
        loadCategories();
    }, [success]);

    const loadCategories = async () => {
        const response = await axios.get(`${API}/categories`);
        setState({ ...state, loadedCategories: response.data });
    };

    const handleTitleChange = e => {
        setState({ ...state, title: e.target.value, error: '', success: '' });
    };

    const handleURLChange = e => {
        setState({ ...state, url: e.target.value, error: '', success: '' });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        // console.table({ title, url, categories, type, medium });
        // use update link based on logged in user role

        let dynamicUpdateUrl;
        if (isAuth() && isAuth().role === 'admin') {
            dynamicUpdateUrl = `${API}/link/admin/${oldLink._id}`;
        } else {
            dynamicUpdateUrl = `${API}/link/${oldLink._id}`;
        }

        try {
            const response = await axios.put(
                dynamicUpdateUrl,
                { title, url, categories, type, medium },
                {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                }
            );
            setState({ ...state, success: 'Link is updated' });
        } catch (error) {
            console.log('LINK SUBMIT ERROR', error);
            setState({ ...state, error: error.response.data.error });
        }
    };

    const handleTypeClick = e => {
        setState({ ...state, type: e.target.value, success: '', error: '' });
    };

    const handleMediumClick = e => {
        setState({ ...state, medium: e.target.value, success: '', error: '' });
    };

    const showMedium = () => (
        <React.Fragment>
            <div className="form-check ml-3">
                <label className="form-check-label">
                    <input
                        type="radio"
                        onClick={handleMediumClick}
                        checked={medium === 'video'}
                        value="video"
                        className="from-check-input"
                        name="medium"
                    />{' '}
                    Video
                </label>
            </div>

            <div className="form-check ml-3">
                <label className="form-check-label">
                    <input
                        type="radio"
                        onClick={handleMediumClick}
                        checked={medium === 'book'}
                        value="book"
                        className="from-check-input"
                        name="medium"
                    />{' '}
                    Book
                </label>
            </div>
        </React.Fragment>
    );

    const showTypes = () => (
        <React.Fragment>
            <div className="form-check ml-3">
                <label className="form-check-label">
                    <input
                        type="radio"
                        onClick={handleTypeClick}
                        checked={type === 'free'}
                        value="free"
                        className="from-check-input"
                        name="type"
                    />{' '}
                    Free
                </label>
            </div>

            <div className="form-check ml-3">
                <label className="form-check-label">
                    <input
                        type="radio"
                        onClick={handleTypeClick}
                        checked={type === 'paid'}
                        value="paid"
                        className="from-check-input"
                        name="type"
                    />{' '}
                    Paid
                </label>
            </div>
        </React.Fragment>
    );

    const handleToggle = c => () => {
        // return the first index or -1
        const clickedCategory = categories.indexOf(c);
        const all = [...categories];

        if (clickedCategory === -1) {
            all.push(c);
        } else {
            all.splice(clickedCategory, 1);
        }
        console.log('all >> categories', all);
        setState({ ...state, categories: all, success: '', error: '' });
    };

    // show categories > checkbox
    const showCategories = () => {
        return (
            loadedCategories &&
            loadedCategories.map((c, i) => (
                <li className="list-unstyled" key={c._id}>
                    <input
                        type="checkbox"
                        checked={categories.includes(c._id)}
                        onChange={handleToggle(c._id)}
                        className="mr-2"
                    />
                    <label className="form-check-label">{c.name}</label>
                </li>
            ))
        );
    };

    // link create form
    const submitLinkForm = () => (
        <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
                <label className="text-muted">Title</label>
                <input type="text" className="form-control p-3" onChange={handleTitleChange} value={title} />
            </div>
            <div className="form-group mb-3">
                <label className="text-muted">URL</label>
                <input type="url" className="form-control p-3" onChange={handleURLChange} value={url} />
            </div>
            <div>
                <button disabled={!token} className="btn btn-oprimary px-4" type="submit">
                    {isAuth() || token ? 'Update' : 'Login to update'}
                </button>
            </div>
        </form>
    );

    return (
        <Layout>
            <div className='container pt-5'>
                <div className="row">
                    <div className="col-md-12">
                        <h1>Update Link</h1>
                        <br />
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-4">
                        <div className="form-group">
                            <label className="text-muted ml-4">Category</label>
                            <ul style={{ maxHeight: '100px', overflowY: 'scroll' }}>{showCategories()}</ul>
                        </div>
                        <div className="form-group">
                            <label className="text-muted ml-4">Type</label>
                            {showTypes()}
                        </div>
                        <div className="form-group">
                            <label className="text-muted ml-4">Medium</label>
                            {showMedium()}
                        </div>
                    </div>
                    <div className="col-md-8">
                        {success && showSuccessMessage(success)}
                        {error && showErrorMessage(error)}
                        {submitLinkForm()}
                    </div>
                </div>
            </div>
        </Layout>
    );
};

Update.getInitialProps = async ({ req, token, query }) => {
    const response = await axios.get(`${API}/link/${query.id}`);
    return { oldLink: response.data, token };
};

export default withUser(Update);
